<?php

namespace DaydreamLab\Cms\Controllers\Language;

use DaydreamLab\JJAJ\Controllers\BaseController;
use DaydreamLab\JJAJ\Helpers\ResponseHelper;
use Illuminate\Support\Collection;
use DaydreamLab\CMS\Services\Language\LanguageService;
use DaydreamLab\Cms\Requests\Language\LanguageRemovePost;
use DaydreamLab\Cms\Requests\Language\LanguageStorePost;
use DaydreamLab\Cms\Requests\Language\LanguageStatePost;
use DaydreamLab\Cms\Requests\Language\LanguageSearchPost;

class LanguageController extends BaseController
{
    public function __construct(LanguageService $service)
    {
        parent::__construct($service);
    }


    public function getItem($id)
    {
        $this->service->getItem($id);

        return ResponseHelper::response($this->service->status, $this->service->response);
    }


    public function getItems()
    {
        $this->service->search(new Collection());

        return ResponseHelper::response($this->service->status, $this->service->response);
    }


    public function remove(LanguageRemovePost $request)
    {
        $this->service->remove($request->rulesInput());

        return ResponseHelper::response($this->service->status, $this->service->response);
    }


    public function state(LanguageStatePost $request)
    {
        $this->service->state($request->rulesInput());

        return ResponseHelper::response($this->service->status, $this->service->response);
    }


    public function store(LanguageStorePost $request)
    {
        $this->service->store($request->rulesInput());

        return ResponseHelper::response($this->service->status, $this->service->response);
    }


    public function search(LanguageSearchPost $request)
    {
        $this->service->search($request->rulesInput());

        return ResponseHelper::response($this->service->status, $this->service->response);
    }
}
